css:
.print-format {
    width: 35mm;
    height: 15mm;
    padding: 0px;
    min-height: 15mm;
    background: white;
}

.barcode-wrapper {
    width: 100%;
    text-align: center;
}

@media print {
    body * {
        visibility: hidden;
    }
    .print-format, .print-format * {
        visibility: visible;
    }
    .print-format {
        position: absolute;
        top: 0;
        left: 0;
        width: 35mm;
        height: 15mm;
        overflow: hidden;
    }
}
